$(document).on("change",".js-change-input",function(){var alias=$(this).data("alias");var value=$(this).val();var name=$(this).attr("name");if($("#"+name+"-"+alias).length){$("#"+name+"-"+alias).val($(this).val())}else{$("#form-main").append('<input type="hidden" id="'+name+"-"+alias+'" name="Menu['+alias+"]["+name+']" value="'+value+'" />');if(!$("#"+alias).length){$("#form-main").append('<input type="hidden" id="'+alias+'" name="Menu['+alias+'][data]" value="" />')}}});