document.querySelector(".generate-pass")?.addEventListener("click",function(){const passwordInput=document.querySelector(".password-input");const showPassBtn=document.querySelector(".show-pass");const newPassword=generatePassword(16);passwordInput.value=newPassword;if(passwordInput.type==="password"){showPassBtn?.click()}});function generatePassword(length){const charset="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()_+~`|}{[]:;?><,./-=";let password="";for(let i=0,n=charset.length;i<length;++i){password+=charset.charAt(Math.floor(Math.random()*n))}return password}